./src/components/views/Members/members.s._style:
.members .members-list {
  margin-top: 1em;
}

.members .member {
  align-items: center;
  padding: .5em .25em;
}

.members .member input[type='checkbox'] {
  display: none;
}

.members .member input[type='checkbox'] + label .checkbox {
  display: block;
  width: 40px;
  height: 25px;
  border-radius: 25px;
  transition: all .3s;
}

.members .member input[type='checkbox'] + label .checkbox:before {
  display: block;
  content: "";
  background-color: #fff;
  width: 25px;
  height: 25px;
  box-shadow: inset 0 0 0 1px #e1e8f0;
  box-sizing: border-box;
  border-radius: 50%;
  transition: inherit;
}

.members .member input[type='checkbox']:not(:checked) + label .checkbox {
  background-color: #eb5563;
}

.members .member input[type='checkbox']:not(:checked) + label .checkbox:before {
  border: 2px solid #eb5563;
}

.members .member input[type='checkbox']:checked + label .checkbox {
  background-color: #ff8d33;
}

.members .member input[type='checkbox']:checked + label .checkbox:before {
  border: 2px solid #ff8d33;
  transform: translateX(15px);
}

.members .member label {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  font-size: 18px;
  line-height: 1;
}

